mapper.select:
select

from t_order_info a
where 1=1

		AND a.cm_id = #{coffeeMakerId}


		AND a.dealer = #{dealer}


		AND a.pay_channel = #{payChannel}


		AND DATE_FORMAT(a.create_date,'%Y-%m-%d') BETWEEN DATE_FORMAT(#{lastStatisticsTime_gte,jdbcType=TIMESTAMP},'%Y-%m-%d') AND DATE_FORMAT(#{lastStatisticsTime_lte,jdbcType=TIMESTAMP},'%Y-%m-%d')


		AND DATE_FORMAT(a.create_date,'%Y-%m-%d') >= DATE_FORMAT(#{lastStatisticsTime_gte,jdbcType=TIMESTAMP},'%Y-%m-%d')


		AND DATE_FORMAT(a.create_date,'%Y-%m-%d') <= DATE_FORMAT(#{lastStatisticsTime_lte,jdbcType=TIMESTAMP},'%Y-%m-%d')

GROUP BY DATE_FORMAT(a.create_date,'%Y-%m-%d') , a.status
ORDER BY DATE_FORMAT(a.create_date,'%Y-%m-%d');
